document.getElementById("create-rule-button").addEventListener("click",(async()=>{const ruleString=document.getElementById("rule-input").value,response=await fetch("/create_rule",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({rule:ruleString})}),result=await response.json();document.getElementById("rule-output").innerText=`Rule Created: ${result.rule_name}, AST: ${result.ast}`})),document.getElementById("evaluate-rule-button").addEventListener("click",(async()=>{const ruleName=document.getElementById("rule-input").value,dataInput=document.getElementById("data-input").value;let userData;try{userData=JSON.parse(dataInput)}catch(error){return void alert("Invalid JSON format in data input.")}const response=await fetch("/evaluate_rule",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({rule:ruleName,data:userData})}),result=await response.json(),outputDiv=document.getElementById("evaluation-output");response.ok?outputDiv.textContent=`Evaluation Result: ${result.result}`:outputDiv.textContent=`Error: ${result.error}`}));